package advent2018;

import org.junit.Test;

import java.util.HashMap;
import java.util.Map;

public class Advent2018Day5Test {

    private String input = "";

    private String testInput = "dabAcCaCBAcCcaDA";
    private String testInput1 = "abBA";

    @Test
    public void testPart1() {
        String output = removeReactingUnits(input);
        System.out.println("Remaining units: " + output.length());
    }

    @Test
    public void testPart2() {
        Integer output = removeUnitAndCheckReactingUnits(input);
        System.out.println("Best reaction: " + output);
    }

    private Integer removeUnitAndCheckReactingUnits(String input) {
        Map<String, Integer> reactionsAfterRemoval = new HashMap<>();
        String removedUnit;
        for (int i = 0; i < input.length(); i++) {
            removedUnit = String.valueOf(input.charAt(i)).toLowerCase();
            if (!reactionsAfterRemoval.containsKey(removedUnit)) {
                String s = new String(input);
                s = s.replaceAll(removedUnit.toLowerCase(), "");
                s = s.replaceAll(removedUnit.toUpperCase(), "");
                String finalReaction = removeReactingUnits(s);
                reactionsAfterRemoval.put(removedUnit, finalReaction.length());
            }
        }
        Integer bestReaction = input.length();
        for (Map.Entry<String, Integer> entry : reactionsAfterRemoval.entrySet()) {
            if (entry.getValue() < bestReaction) {
                bestReaction = entry.getValue();
            }
        }
        return bestReaction;
    }

    private String removeReactingUnits(String testInput) {
        StringBuilder sb = new StringBuilder(testInput);
        int length = sb.length();
        for (int i = 0; i < length; i++) {
            if (i + 1 < length) {
                Character firstLetter = sb.charAt(i);
                Character comparisonLetter = sb.charAt(i + 1);
                if (compareLettersAreCancelling(firstLetter, comparisonLetter)) {
                    sb.deleteCharAt(i);
                    sb.deleteCharAt(i);
                    length = sb.length();
                    i = -1;
                }
            }
        }
        return sb.toString();
    }

    private boolean compareLettersAreCancelling(Character firstLetter, Character comparisonLetter) {
        if (Character.isLowerCase(firstLetter) && Character.isLowerCase(comparisonLetter)) {
            return false;
        } else if (Character.isUpperCase(firstLetter) && Character.isUpperCase(comparisonLetter)) {
            return false;
        } else {
            if (firstLetter.toString().equalsIgnoreCase(comparisonLetter.toString())) {
                return true;
            }
        }
        return false;
    }
}
